<body class="zh_HK">
    <header class="wx-header__container" id="wx-header" style="top: 0px;">
      <div class="branch-journeys-top"></div>
      <section class="wx-header__core border-bottom-line" id="top-header-core">
        <section class="wx-header__left-section">
          
      <button type="button" class="wx-header__drawer-button browseMenuMobile" onfocus="AR.header.showPrimaryNav(`browseMenuMobile` === 'browseMenuDesktop');">
        <!-- TODO review and action depending on lists experiment success -->
        
        <i class="iconAct-Menu"></i>
          
          <div class="wx-header__drawer-button-label">
            Browse
          </div>
          
        
        
      </button>

          <div class="wx-header__logo-container">
            
      <a class="wx-header__logo" ereventtype="click" ercategory="image" ername="image_click" erlabel="Wapple logo" ervalue="https://www.woolworths.com.au" onclick="AR.header.onWappleClicked(event)" href="/">
        <picture>
          <source media="(min-width:  768px)" srcset="https://cdn1.woolworths.media/content/content/icon-header-logo.green.png">
          <source media="(max-width:  767px)" srcset="https://cdn1.woolworths.media/content/content/icon-header-logo-only.png">
          <img class="img" src="https://cdn1.woolworths.media/content/content/icon-header-logo-only.png" fetchpriority="high" aria-label="Wapple Woolworths logo navigate to home page" alt="Woolworths Online Home">
        </picture>
      </a>

          </div>      
        </section>
        <section class="wx-header__item">
          <div class="" id="wx-lists">
      <button type="button" class="wx-header__drawer-button lists" onclick="AR.header.navigateToListsPage(event)" onfocus="AR.header.showPrimaryNav(`lists` === 'browseMenuDesktop');">
        <!-- TODO review and action depending on lists experiment success -->
        
        <div class="wx-header__icon lists ">
          
          <img class="img" width="20" height="20" src="https://cdn1.woolworths.media/wowssr/a10/browser/assets/images/header/icon-list.svg" fetchpriority="high" alt="">
          
        </div>
        
        
      </button>
     </div>
          <div class="" id="wx-sign-in">
      <button type="button" class="wx-header__drawer-button signIn">
        <!-- TODO review and action depending on lists experiment success -->
        
        <div class="wx-header__icon signIn iconAct-Account_Login_Filled">
          
        </div>
        
        
      </button>
    </div>
          <div class="hidden" id="wx-my-account">
      <button type="button" class="wx-header__drawer-button myAccount" onclick="AR.header.toggle(event, `drawer`, `myAccount`, ``, `right`);" onfocus="AR.header.showPrimaryNav(`myAccount` === 'browseMenuDesktop');">
        <!-- TODO review and action depending on lists experiment success -->
        
        <div class="wx-header__icon myAccount ">
          
        </div>
        
        
      </button>
     </div>
          <button id="header-view-cart-button" class="button wx-header__checkout " onclick="AR.headerCheckout.toggleCartOpen(event)" type="button" ereventtype="click" ercategory="nav-primary" erlabel="Checkout: View Cart" ervalue="Cart Close">
        <span class="sr-only">View cart button</span>
        <div class="wx-header__checkout-cartLeft">
          <i class="iconShop-Cart-Filled wx-header__checkout-cartIcon">
          </i>
        </div>
        <div class="wx-header__checkout-cartRight">
          <div class="wx-header__checkout-orderTotal wx-header__checkout-orderTotalContainer" id="wx-header-checkout-amount">
            <div class="wx-header__checkout-orderAmount order-total-slider" aria-hidden="true">

              <div class="order-total-number"></div>
              <div class="order-total-number current">
                <div>$00.00</div>
              </div>
            </div>
            <span class="sr-only">
              <span>Your Cart has</span> 
              <span id="wx-header-checkout-quantity-sr">0</span>
              <span id="wx-header-checkout-plural-sr">item</span> worth
              <span id="wx-header-checkout-amount-sr">$0</span> in total
            </span>
          </div>
          <div id="wx-header-cart-quantity-pill" class="wx-header__checkout-quantityPill wx-header__checkout-quantityPillHidden" aria-hidden="true">
              <div class="wx-header__checkout-quantityPillContainer">
                <div class="quantity-pill-slider">
                  <div class="quantity-pill-number"></div>
                  <div class="quantity-pill-number current">
                    <div>0</div>
                  </div>
                </div>
              </div>
            </div>
        </div>
      </button>

        </section>
      </section>

      <section class="wx-header__core wx-header__search" id="search-header-core" style="height: 60px;">
        <div id="wx-search-container" class="wx-header__search-panel">
      <form novalidate="" action="search" class="wx-header__search-form">
        <div id="wx-search-wrapper" class="wx-header__search-wrapper">
          <div tabindex="-1" class="autocomplete">
            <div class="autocomplete-searchBar">
              <input id="wx-headerSearch" name="headerSearch" type="search" autocomplete="off" class="wx-header__search-searchBox" placeholder="Search products, recipes &amp; ideas" role="combobox" aria-owns="wx-header__search-results" aria-haspopup="true" aria-autocomplete="both" aria-label="Search products, recipes &amp; ideas" aria-expanded="false" aria-activedescendant="" onfocus="AR.headerSearch.searchFocus()" onfocusout="AR.headerSearch.searchFocusOut(event)" oninput="AR.headerSearch.searchOnInput()" onkeydown="AR.headerSearch.searchKeydown(event)">
              <span tabindex="0" id="mobile-search-tab-guide" onfocus="AR.headerSearch.focusOnCancel()"></span>
              <div class="autocomplete-dropdownWrapper wx-header__search-dropdownWrapper" fs-scroll-lock-ignore="">
                <ul id="wx-search-result" class="autocomplete-dropdown wx-header__search-dropdown" role="listbox" aria-labelledby="wx-headerSearch" tabindex="-1" onscroll="AR.headerSearch.shiftFocus()" onfocusout="AR.headerSearch.searchFocusOut(event)"></ul>
              </div>
            </div>
            <div class="autocomplete-actionButtonWrapper">
              <button id="wx-button-header-search-clear" aria-label="Clear Search" type="button" value="Clear Search" class="autocomplete-actionButton wx-header__search-actionButton wx-header__search-searchBoxClearButton d-none" onclick="AR.headerSearch.clearButtonClick()" onmouseover="AR.headerSearch.childButtonHover(true)" onmouseout="AR.headerSearch.childButtonHover(false)">
                <i class="icon iconAct-Close_Cancel"></i>
              </button>
              <button id="wx-button-header-search-find" aria-label="Search" type="button" value="Search" class="autocomplete-actionButton autocomplete-searchButton" onclick="AR.headerSearch.searchButtonClick(event)" onmouseover="AR.headerSearch.childButtonHover(true)" onmouseout="AR.headerSearch.childButtonHover(false)">
                <i class="iconAct-Search search-button-icon"></i>
              </button>

            </div>
          </div>
        </div>
      </form>
    </div>

        <button id="wx-header__search-cancel" onclick="AR.header.closeAllMenus()" class="">
          Cancel
        </button>
      </section>
    </header>





